import { React, useState, useEffect} from "react";
import axios from "axios";

const Search = () => {

    const [pokemonName, setPokemonName] = useState("")

    function lowName(string) {
        return string.charAt(0).toLowerCase() + string.slice(1);
    }

    const searchPokemon = (e) => {
        e.preventDefault()

        const lowerName = lowName(pokemonName)

        axios.get(`https://pokeapi.co/api/v2/pokemon/${lowerName}`)
        .then((res) => {
            console.log(res.data)
        })
    }

    return(
        <div className="search">
            <form onSubmit={searchPokemon}>
                <input type="text" placeholder="Pokemon Name" value={pokemonName} onChange={(e)=>setPokemonName(e.target.value)} />
                <button type="submit">Search</button>
            </form>
        </div>
    )
}

export default Search
